INSERT INTO `tl_faq` (`id`, `addImage`, `overwriteMeta`, `fullsize`, `addEnclosure`, `noComments`, `published`, `pid`, `sorting`, `tstamp`, `question`, `alias`, `author`, `answer`, `pageTitle`, `robots`, `description`, `singleSRC`, `alt`, `imageTitle`, `size`, `imageUrl`, `caption`, `floating`, `enclosure`) VALUES (5, 0, 0, 0, 0, 0, 1, 4, 128, 1708598123, 'Ist Contao das richtige Content Management System für mich?', 'ist-contao-das-richtige-content-management-system-fur-mich', 4, '<p>Contao ist sehr beliebt, weil es sehr viele Funktionen von Hause aus mitbringt. In der Übersicht über die Inhaltselemente und der Module kann man sich einen guten Eindruck verschaffen. Außerdem bringt Contao einen hohen Sicherheitsstandart mit und nimmt Barrierefreiheit sehr ernst. Dieses CMS ist gut skalierbar. Es können leicht kleinere Seiten erstellt werden und auch komplexe Seiten. Es gibt viele nützliche Erweiterungen, auch eine Shop-Erweiterung (isotope e-Commerce).</p>', '', '', NULL, NULL, '', '', '', '', '', 'above', NULL);
INSERT INTO `tl_faq` (`id`, `addImage`, `overwriteMeta`, `fullsize`, `addEnclosure`, `noComments`, `published`, `pid`, `sorting`, `tstamp`, `question`, `alias`, `author`, `answer`, `pageTitle`, `robots`, `description`, `singleSRC`, `alt`, `imageTitle`, `size`, `imageUrl`, `caption`, `floating`, `enclosure`) VALUES (6, 0, 0, 0, 0, 0, 1, 4, 64, 1708598235, 'Ist Contao besser als Typo3?', 'ist-contao-besser-als-typo3', 4, '<p>Für Typo3 muss man TypoScript können. Das ist meiner Meinung nach eine sehr große Hürde. Contao bietet außerdem einen Long-Term-Support für 4 Jahre an. Das ist besser als Typo3.</p>', '', '', NULL, NULL, '', '', '', '', '', 'above', NULL);
INSERT INTO `tl_faq` (`id`, `addImage`, `overwriteMeta`, `fullsize`, `addEnclosure`, `noComments`, `published`, `pid`, `sorting`, `tstamp`, `question`, `alias`, `author`, `answer`, `pageTitle`, `robots`, `description`, `singleSRC`, `alt`, `imageTitle`, `size`, `imageUrl`, `caption`, `floating`, `enclosure`) VALUES (7, 0, 0, 0, 0, 0, 1, 4, 32, 1708598371, 'Wordpress oder Contao?', 'wordpress-oder-contao', 4, '<p>Wenn ich nur einen Blog installieren möchte wäre Wordpress die richtige Wahl. In Punkten Sicherheit und den Funktionen was Contao ohne Erweiterungen mitbringt liegt Contao ganz klar vorn. Meiner Meinung nach ist auch die Dateiverwaltung bei Contao schöner gestaltet.</p>', '', '', NULL, NULL, '', '', '', '', '', 'above', NULL);
